<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.$connectDB = startup();// Получаем статью по ID$article = articles_get($connectDB, $_GET["id"]);$art_title = $article["title"];$art_content = $article["content"];// Переменные$top_title = "Консоль редактора | Редактирование статьи";$title = "Консоль редактора";$cont_title = "Редактирование статьи";if(isset($_GET["id"])) {    // Проверка на присутствие записи с указанным ID    if($article["id"] == $_GET["id"]) {        // Переменная для вывода ошибки над формой        $error = false;        // Обработка отправки формы.        if (isset($_POST["send-editArticle"])) {            if (articles_edit($connectDB, $_GET["id"], $_POST['title'], $_POST['content'])) {                header('Location: editor.php');                exit;            }            else {                $art_title = $_POST['title'];                $art_content = $_POST['content'];                $error = true;            }        }    } else {        header('Location: editor.php');        exit;    }} else {    header('Location: editor.php');    exit;   }// Основной шаблон->Менюшка$main_menu = Templater("theme/main_menu.php", array(    "current" => "$title",));// Основной шаблон->Центральная часть->Страница с формой->Хлебные крошки$breadcrumbs = Templater("theme/breadcrumbs.php", array(    "link" => "edit.php?id=".$_GET["id"],    "cont_title" => $cont_title));// Основной шаблон->Центральная часть->Страница с формой->Форма$form = Templater("theme/form_edit.php", array(    "error" => "$error",    "title" => "$art_title",    "content" => "$art_content"));// Основной шаблон->Центральная часть->Страница с формой$form_page = Templater("theme/form.php", array(    "title" => $cont_title,    "breadcrumbs" => "$breadcrumbs",    "form" => $form));// Основной шаблон->Центральная часть$content = Templater("theme/middle_part.php", array(    "width" => "content_full-width",    "content" => "$form_page",    "sidebar" => ""));// Основной шаблон$page = Templater("theme/main.php", array(    "top_title" => "$top_title",    "title" => "$title",    "main_menu" => "$main_menu",    "content" => "$content"));// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод шаблона.echo $page;