<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.$connectDB = startup();// Извлечение статей.$articles = articles_all($connectDB);// Переменные$top_title = "Консоль редактора";$title = "Консоль редактора";$error = false;if(($articles == false) || (count($articles) <= 0)) {    $error = true;}// Обработка запроса на удалениеif(isset($_GET["del"])) {    // Проверка на присутствие записи с указанным ID    for($i = 0; $i < count($articles); $i++) {        if($articles[$i]["id"] == $_GET["del"]) {            $found = true;            break;        }        else $found = false;    }    if($found == true) {        if(articles_delete($connectDB, $_GET["del"])) {            header('location: editor.php');            exit;        }    }}// Основной шаблон->Менюшка$main_menu = Templater("theme/main_menu.php", array(    "current" => "$title",));// Основной шаблон->Центральная часть->Вывод заголовков статей$title_list = Templater("theme/title_list.php", array(    "error" => "$error",    "articles" => $articles));// Основной шаблон->Центральная часть->Сайдбар$sidebar = Templater("theme/sidebar.php", array());// Основной шаблон->Центральная часть$content = Templater("theme/middle_part.php", array(    "width" => "",    "content" => "$title_list",    "sidebar" => "$sidebar"));// Основной шаблон$page = Templater("theme/main.php", array(    "top_title" => "$top_title",    "title" => "$title",    "main_menu" => "$main_menu",    "content" => "$content"));// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод шаблона.echo $page;