<?php    include_once "configs/config.php";    // Анализ URL. Создаем массив params    $params = array();    if(isset($_GET["q"])){        $info = explode("/", $_GET["q"]);        foreach ($info as $v) {            if($v != "") $params[] = $v;        }    }    $action = "Action_";    if(isset($params[2])) {        $action .= (isset($params[1]))?$params[1]:"article";    } else $action .= (isset($params[1]))?$params[1]:"index";    $controller = "";    if(isset($params[0])) $controller = $params[0];    switch($controller) {        case "articles": $c = new C_Article();            break;        case "users": $c = new C_User();            break;        case "page": $c = new C_Page();            break;        default:            $c = new C_Article();    }    $c->Request($action, $params);?>