<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.$connectDB = startup();// Переменные$top_title = "Главная";$title = "Добро пожаловать на мой сайт!!!";$error = false;if(isset($_GET["id"])) {    $id = $_GET["id"];    if(is_correctID($id)) {        $article = articles_get($connectDB, $id);        if($article) {            // Переопределяем переменные            $top_title = $article["title"];            // Основной шаблон->Центральная часть->Вывод статьи            $article = Templater("theme/article.php", array(                "title" => "$top_title",                "text" => $article["content"]            ));            // Основной шаблон->Центральная часть            $content = Templater("theme/middle_part.php", array(                "width" => "content_full-width",                "content" => "$article",                "sidebar" => ""            ));        } else {            header("location: index.php");            exit;        }    } else {        header("location: index.php");        exit;    }} else {    // Извлечение статей.    $articles = articles_all($connectDB);    if(($articles == false) || (count($articles) <= 0)) {        $error = true;    }    // Основной шаблон->Центральная часть->Вывод анонсов статей    $prev_list = Templater("theme/prev_list.php", array(        "error" => "$error",        "articles" => articles_intro($articles)    ));    // Основной шаблон->Центральная часть    $content = Templater("theme/middle_part.php", array(        "width" => "content_full-width",        "content" => "$prev_list",        "sidebar" => ""    ));}// Основной шаблон->Менюшка$main_menu = Templater("theme/main_menu.php", array(    "current" => "$top_title",));// Основной шаблон$page = Templater("theme/main.php", array(    "top_title" => "$top_title",    "title" => "$title",    "main_menu" => "$main_menu",    "content" => "$content"));// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод шаблона.echo $page;