<?phpfunction __autoload($name) {    include_once "inc/$name.php";}$action = "Action_";if(isset($_GET["id"])) {    $action .= isset($_GET["a"])?$_GET["a"]:"article";} else $action .= isset($_GET["a"])?$_GET["a"]:"index";$controller = "";if(isset($_GET["c"])) $controller = $_GET["c"];switch($controller) {    case "article": $c = new C_Article();        break;    default:        $c = new C_Article();}$c->Request($action);?>