<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.$connectDB = startup();// Переменные$top_title = "Консоль редактора | Добавление статьи";$title = "Консоль редактора";$cont_title = "Новая статья";$error = false;$art_title = "";$art_content = "";// Обработка отправки формы.if (isset($_POST["send-addArticle"])) {	if (articles_new($connectDB, $_POST['title'], $_POST['content'])) {		header('Location: editor.php');		exit;	}	$art_title = $_POST['title'];	$art_content = $_POST['content'];	$error = true;} else {	$title = '';	$content = '';	$error = false;}// Основной шаблон->Менюшка$main_menu = Templater("theme/main_menu.php", array(	"current" => "$title",));// Основной шаблон->Центральная часть->Страница с формой->Хлебные крошки$breadcrumbs = Templater("theme/breadcrumbs.php", array(	"link" => "new.php",	"cont_title" => $cont_title));// Основной шаблон->Центральная часть->Страница с формой->Форма$form = Templater("theme/form_new.php", array(	"error" => "$error",	"title" => "$art_title",	"content" => "$art_content"));// Основной шаблон->Центральная часть->Страница с формой$form_page = Templater("theme/form.php", array(	"title" => $cont_title,	"breadcrumbs" => "$breadcrumbs",	"form" => $form));// Основной шаблон->Центральная часть$content = Templater("theme/middle_part.php", array(	"width" => "content_full-width",	"content" => "$form_page",	"sidebar" => ""));// Основной шаблон$page = Templater("theme/main.php", array(	"top_title" => "$top_title",	"title" => "$title",	"main_menu" => "$main_menu",	"content" => "$content"));// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод шаблона.echo $page;